import React from 'react';
import '../Styles/stock.css';

function Stock(productsArray) {
  let products = productsArray.productsArray;

  const handleInputChange = (e, productId, field) => {
    const { value } = e.target;
    const updatedProducts = products.map((product) => {
      if (product.id === productId) {
        return {
          ...product,
          [field]: value,
        };
      }
      return product;
    });

    console.log(updatedProducts);
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Id</th>
          <th>NombreProducto</th>
          <th>Precio</th>
          <th>Cantidad</th>
          <th>MÃ­nimoStock</th>
          <th>Accion</th>
        </tr>
      </thead>
      <tbody>
        {products.map((product) => (
          <tr key={product.id}>
            <td>
              <input type="text" disabled value={product.id} />
            </td>
            <td>
              <input type="text" disabled value={product.name} />
            </td>
            <td>
              <input
                type="number"
                min={0}
                value={product.price}
                onChange={(e) => handleInputChange(e, product.id, 'price')}
              />
            </td>
            <td>
              <input
                type="number"
                min={0}
                value={product.amount}
                onChange={(e) => handleInputChange(e, product.id, 'amount')}
              />
            </td>
            <td>
              <input
                type="number"
                min={0}
                value={product.minStock}
                onChange={(e) => handleInputChange(e, product.id, 'minStock')}
              />
            </td>
            <td>
              <button>Editar</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default Stock;
