import React, { useState } from 'react';
import '../Styles/stock.css';

function Stock(productsArray) {
  const [products, setProducts] = useState(productsArray.productsArray);

  const handleInputChange = (e, productId, field) => {
    const updatedProducts = products.map((product) => {
      if (product.id === productId) {
        return { ...product, [field]: e.target.value };
      }
      return product;
    });
    setProducts(updatedProducts);
  };

  const [formData, setFormData] = useState(productsArray.productsArray[0]);
  const handleSubmit = (e) => {
    e.preventDefault();

    const fetchData = async () => {
      console.log(formData)
      try {
        const response = await fetch('http://localhost:9000/administrator', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        const data = await response.json();
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Id</th>
          <th>NombreProducto</th>
          <th>Precio</th>
          <th>Cantidad</th>
          <th>MÃ­nimoStock</th>
          <th>Accion</th>
        </tr>
      </thead>
      <tbody>
        {products.map((product) => (
          <tr key={product.id}>
            <form onSubmit={handleSubmit}>
            <td>
              <input type="text" disabled value={product.id} />
            </td>
            <td>
              <input type="text" disabled value={product.name} />
            </td>
            <td>
              <input
                type="number"
                min={0}
                value={product.price}
                onChange={(e) =>
                  handleInputChange(e, product.id, 'price')
                }
              />
            </td>
            <td>
              <input
                type="number"
                min={0}
                value={product.amount}
                onChange={(e) =>
                  handleInputChange(e, product.id, 'amount')
                }
              />
            </td>
            <td>
              <input
                type="number"
                min={0}
                value={product.minStock}
                onChange={(e) =>
                  handleInputChange(e, product.id, 'minStock')
                }
              />
            </td>
            <td>
              <button type='submit'>Editar</button>
            </td>
            </form>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default Stock;
