import React, {useEffect, useState} from 'react';
import Stock from './stock';
import AdministratorForm from './administratorForm';
import '../Styles/administrator.css';

function Administrator() {
  const [admin, setAdmin] = useState(null);
  const [displayComponent, setDisplayComponent] = useState(null);

  const handleButtonClick = (componentName) => {
    if (componentName === 'administratorForm') {
      setDisplayComponent(<AdministratorForm />);
    } else if (componentName === 'stock') {
      setDisplayComponent(<Stock />);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:9000/administrator');
        const data = await response.json();
        setAdmin(data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  console.log(admin);

  return (
    <>
      {admin && (
        <div className='adminData'>
          <h2>Bienvenido {admin.admin[0].name}</h2>
          <p>Desde aquí puedes editar tus datos y el stock de productos</p>
          {/* <button onClick={() => handleButtonClick('administratorForm')}>Editar Información Administrador</button> */}
          {/* <button onClick={() => handleButtonClick('stock')}>Editar Inventario</button> */}
          {/* {displayComponent} */}
          {/* <AdministratorForm admin={admin.admin} /> */}
          <form method='post'>
            <div>
              <label htmlFor='name'>Nombre</label>
              <input type='text' name='name' id='name' disabled value={admin.admin[0].name} />
            </div>
            <div>
              <label htmlFor='email'>Email</label>
              <input type='email' name='email' id='email' value={admin.admin[0].email} />
            </div>
            <div>
              <label htmlFor='password'>Contraseña</label>
              <input type='password' name='password' id='password' value={admin.admin[0].password} />
            </div>
            <div>
              <label htmlFor='phone'>Teléfono</label>
              <input type='tel' name='phone' id='phone' value={admin.admin[0].phone} />
            </div>
            <div>
              <label htmlFor='address'>Dirección</label>
              <input type='text' name='address' id='address' value={admin.admin[0].address} />
            </div>
            <button type='submit'>Editar</button>
          </form>
        <Stock productsArray={admin.products} />
        </div>
      )}
    </>
  )
}

export default Administrator;
