import React, {useEffect, useState} from 'react';
import Stock from './stock';
import '../Styles/administrator.css';

// function GetDataAdmin(admin) {

//   return (
//     <>
//     <div className='adminData'>
//         <h2>Bienvenido {admin.admin[0].name}</h2>
//         <form method='post'>
//           <div>
//             <label htmlFor='name'>Nombre</label>
//             <input type='text' name='name' id='name' disabled value={admin[0].name} />
//           </div>
//           <div>
//             <label htmlFor='email'>Email</label>
//             <input type='email' name='email' id='email' value={admin[0].email} />
//           </div>
//           <div>
//             <label htmlFor='password'>Contraseña</label>
//             <input type='password' name='password' id='password' value={admin[0].password} />
//           </div>
//           <div>
//             <label htmlFor='phone'>Teléfono</label>
//             <input type='tel' name='phone' id='phone' value={admin[0].phone} />
//           </div>
//           <div>
//             <label htmlFor='address'>Dirección</label>
//             <input type='text' name='address' id='address' value={admin[0].address} />
//           </div>
//           <div><button type='submit'>Editar</button></div>
//         </form>
//       </div>
//       </>
//   )
// }

function Administrator() {
  const [admin, setAdmin] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:9000/administrator');
        const data = await response.json();
        setAdmin(data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  console.log(admin);

  return (
    <>
      <div className='adminData'>
        <h2>Bienvenido {admin.admin[0].name}</h2>
        <form method='post'>
          {/* <div> */}
            <label htmlFor='name'>Nombre</label>
            <input type='text' name='name' id='name' disabled value={admin.admin[0].name} />
          {/* </div> */}
          {/* <div> */}
            <label htmlFor='email'>Email</label>
            <input type='email' name='email' id='email' value={admin.admin[0].email} />
          {/* </div> */}
          {/* <div> */}
            <label htmlFor='password'>Contraseña</label>
            <input type='password' name='password' id='password' value={admin.admin[0].password} />
          {/* </div> */}
          {/* <div> */}
            <label htmlFor='phone'>Teléfono</label>
            <input type='tel' name='phone' id='phone' value={admin.admin[0].phone} />
          {/* </div> */}
          {/* <div> */}
            <label htmlFor='address'>Dirección</label>
            <input type='text' name='address' id='address' value={admin[0].address} />
          {/* </div> */}
          <div><button type='submit'>Editar</button></div>
        </form>
      </div>
      {admin && (
        <>
          <Stock productsArray={admin.products} />
        </>
      )}
    </>
  )
}

export default Administrator;
